function ExpenseDate(props) {
  const month = props.date.toLocaleString("en-US", {
    month: "long",
  });
  const year = props.date.getFullYear();
  const day = props.date.toLocaleString("en-US", {
    day: "2-digit",
  });
}

export default ExpenseDate;
